catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	if(newValue == 'true'){
		g_form.setVisible('seriousness_of_the_wound', true);
		g_form.setVisible('how_does_the_resident_feel', true);
		g_form.setVisible('please_give_a_comment_to_the_new_wound', true);
		g_form.setMandatory('seriousness_of_the_wound', true);
		g_form.setVisible('how_does_the_resident_feel', true);
		g_form.setVisible('please_give_a_comment_to_the_new_wound', true);
	}
		if(newValue == 'false'){
		g_form.setMandatory('seriousness_of_the_wound', false);
		g_form.setMandatory('how_does_the_resident_feel', false);
		g_form.setMandatory('please_give_a_comment_to_the_new_wound', false);
		g_form.setVisible('seriousness_of_the_wound', false);
		g_form.setVisible('how_does_the_resident_feel', false);
		g_form.setVisible('please_give_a_comment_to_the_new_wound', false);

	}
   //Type appropriate comment here, and begin script below

}
