sysauto_script.script:
//Script that automatically creates entries in the "Shift"-table so that enduser do not have to create them in order to be able to assign shifts that are //connected to this entry

var amountOfDaysInMonth = new GlideDateTime().getDayOfMonthLocalTime();
var amountOfDaysToAdd = 0;

//Iterate over the current month
for( i = 0; i < amountOfDaysInMonth; i++){

	//Get current date
	var gdt = new GlideDateTime().getDate();
	gdt.addDaysUTC(amountOfDaysToAdd);

	//Create new shift entry using the GlideRecord Object and setting the date object using the amoundOfDaysToAdd-variable
	var newShift = new GlideRecord('x_797485_caretake_shift');
	newShift.setValue('date', gdt);
	newShift.insert;

	amountOfDaysToAdd = amountOfDaysToAdd + 1;

}
